mapper.insert:
INSERT INTO t_dc_collection_tasks (
    id, name, description, source_datasource_id, target_datasource_id, config, status,
    schedule_expression, retry_count, timeout_seconds, max_records, sort_field,
    last_execution_id, created_at, updated_at, created_by, updated_by
) VALUES (
    #{id}, #{name}, #{description}, #{sourceDataSourceId}, #{targetDataSourceId}, #{config}, #{status},
    #{scheduleExpression}, #{retryCount}, #{timeoutSeconds}, #{maxRecords}, #{sortField},
    #{lastExecutionId}, #{createdAt}, #{updatedAt}, #{createdBy}, #{updatedBy}
)

mapper.select:
SELECT 
FROM t_dc_collection_tasks
WHERE source_datasource_id = #{dataSourceId} 
   OR target_datasource_id = #{dataSourceId}
ORDER BY created_at DESC

SELECT 
FROM t_dc_collection_tasks


        AND status = #{status}


        AND name LIKE CONCAT('%', #{name}, '%')


        AND source_datasource_id = #{sourceDataSourceId}


        AND target_datasource_id = #{targetDataSourceId}


ORDER BY created_at DESC

    LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM t_dc_collection_tasks


        AND status = #{status}


        AND name LIKE CONCAT('%', #{name}, '%')


        AND source_datasource_id = #{sourceDataSourceId}


        AND target_datasource_id = #{targetDataSourceId}

mapper.update:
UPDATE t_dc_collection_tasks 
SET status = #{status}, updated_at = NOW()
WHERE id = #{id}

UPDATE t_dc_collection_tasks 
SET last_execution_id = #{lastExecutionId}, updated_at = NOW()
WHERE id = #{id}
