mapper.select:
SELECT 
FROM t_dm_dataset_files
WHERE dataset_id = #{datasetId}
  AND status = #{status}
ORDER BY upload_time DESC

SELECT 
FROM t_dm_dataset_files
WHERE dataset_id = #{datasetId}
  AND file_type = #{fileType}
ORDER BY upload_time DESC

SELECT 
FROM t_dm_dataset_files
WHERE dataset_id = #{datasetId}


    AND file_type = #{fileType}


    AND status = #{status}

ORDER BY upload_time DESC
