mapper.select:
SELECT operator_id as id, operator_name as name, description, version, inputs, outputs, runtime, settings, is_star, created_at, updated_at FROM ( SELECT operator_id, operator_name, description, version, inputs, outputs, runtime, settings, is_star, created_at, updated_at, ROW_NUMBER() OVER ( PARTITION BY operator_id -- 按 id 分组 ORDER BY created_at DESC -- 按创建时间降序排列 (最新时间在前) ) AS rn -- 为每组内的行分配序号 FROM v_operator
AND operator_name LIKE CONCAT('%', #{operatorName}, '%')
AND category_id IN
#{category}
AND is_star = #{isStar}
) ranked_data WHERE rn = 1 LIMIT ${size} OFFSET ${offset};
